import React, { Component } from 'react';
import Chart from 'chart.js/auto';

export default class LineChart extends Component {

	chartRef = React.createRef();

	componentDidMount() {
		const ctx = this.chartRef.current.getContext("2d");

		new Chart(ctx, {
			type: "bar",
			data: {
				labels: ["2018", "2019", "2020", "2021", "2022",],
				datasets: [{
					data: [4355,4468,4986,5720,6044],
					label: 'Revenue',
					backgroundColor: [
					"#fff",
					"#fff",
					"#fff",
					"#fff",
					"gold"
					],
					borderColor: [
				      '#000',
				      '#000',
				      '#000',
				      '#000',
				      '#000',
				    ],
				    borderWidth: 1
				}
				]
			},
		});
	}
	render() {
		return (
			<div>
				<canvas
				id="myChart"
				ref={this.chartRef}
				/>
			</div>
			)
	}
}
