import React, { Component } from 'react';
import Chart from 'chart.js/auto';
import ChartDataLabels from 'chartjs-plugin-datalabels';

export default class RevenueChart extends Component {

	chartRef = React.createRef();

	componentDidMount() {
		const ctx = this.chartRef.current.getContext("2d");

		new Chart(ctx, {
			type: "bar",
			data: {
				labels: ["2018", "2019", "2020", "2021", "2022",],
				datasets: [{
					data: [4355000,4468000,4986000,5720000,6044000],
					label: 'Revenue',
					backgroundColor: [
					"#fff",
					"#fff",
					"#fff",
					"#fff",
					"gold"
					],
					borderColor: [
				      'grey',
				      'grey',
				      'grey',
				      'grey',
				      '#000',
				    ],
				    borderWidth: {
				    	left: 5,
				    	top: 1
				    }
				}
				]
			},
			plugins: [ChartDataLabels],
			options: {
				responsive: true,
			    plugins: {
			        legend: {
			            display: false
			        },
			        datalabels: {
				        color: [
							"grey",
							"grey",
							"grey",
							"grey",
							"#000"
							],
				        anchor: 'end',
			            align: 'top',
			            formatter: Math.round,
			            font: {
			                weight: [
								"normal",
								"normal",
								"normal",
								"normal",
								"bold"
								],
			            }
				      }
					},
			    scales: {
					x: {
					    grid: {
					      display: false
					    }
					},
					y: {
					      display: false
					}
				},
			}
		});
	}
	render() {
		return (
			<div style='height: 500px'>
				<canvas
				id="myChart"
				ref={this.chartRef}
				/>
			</div>
			)
	}
}
